import React, { useState } from "react";
import './App.css'

const App= () =>{
         

        let newTime = new Date().toLocaleTimeString();
        const [cTime, setCtime] = useState(newTime)





        return(
                <div>
                        <h1>{cTime}</h1>
                        <button onClick={setCtime}>Get Time</button>
                </div>
        )
}

export default App;
