const App= ()=>{
    const [addItem, setAddItem] = useState([]);
    


    const addNote = (note) => {
        // alert("I am clicked");
      setAddItem((prevData)=>{
          return [...prevData,note];
      })
       
     console.log(note);
    }
    return(
        <div>
        <Header/>
        
        <CreateNote 
          passNote = {addNote}
        />
        <Note/>
        <Footer/>

        </div>
    )
}
